class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        std::set<int>st;
        for(int i=0;i<n;i++)st.insert(i);
        std::vector<int>ans;
        for(auto pii : queries){
            int u = pii[0],v = pii[1];
            assert(u<v);
            if(u==v)continue;
            auto it = st.lower_bound(u);
            it = std::next(it);
            while(it!=st.end() && *it<v){
                it = st.erase(it);
            }
            ans.push_back(st.size()-1);
        }
        return ans;

    }
};
