class uf{
public:
    vector<int> f;
    uf(int n): f(n){iota(f.begin(), f.end(), 0);}
    int get(int x) {
        if(x == f[x]) return x;
        return f[x] = get(f[x]);
    }
    void unite(int x, int y){
        x = get(x), y = get(y);
        f[y] = x;
    }  
};

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> ans;
        vector<vector<int>> g(n);
        vector<int> dis(n);
        set<int> ms;
        for(int i = 0; i + 1 < n; i++) g[i].push_back(i + 1), ms.insert(i);

        for(auto& e: q) {
            int u = e[0], v = e[1];
            vector<int> te;
            for(auto it = ms.lower_bound(u); it != ms.end() && *it <= v; it = next(it)) {
                if(*it > u && *it < v) te.push_back(*it);

            }
            for(auto& c: te) ms.erase(c);
            ans.push_back(ms.size());
        }
        return ans;


    }
};
