class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> S;
        for (int i = 0; i < n; i++){
            S.insert(i);
        }
        vector<int> ans;
        for (auto q : queries){
            int x = q[0];
            int y = q[1];
            auto it = S.upper_bound(x);
            while(*it < y){
                it = S.erase(it);
            }
            ans.push_back(S.size() - 1);

        }
        return ans;
    }
};
