class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> res;
        set<int> s;
        for (int i = 0; i < n; i++) {
            s.insert(i);
        }
        for (auto& q : queries) {
            auto it = s.lower_bound(q[0]);
            if (it != s.begin()) {
                it--;
            }
            while (it != s.end()) {
                if (*it > q[0] && *it < q[1]) {
                    auto temp = it;
                    it++;
                    s.erase(temp);
                    continue;
                }
                if (*it >= q[1]) {
                    break;
                }
                it++;
            }
            res.push_back(s.size() - 1);
        }
        return res;
    }
};
