class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for (int i = 1; i < n; ++i) {
            st.insert(i);
        }
        vector<int> ans;
        for (auto& query : queries) {
            int a = query[0];
            int b = query[1];
            auto it = st.upper_bound(a);
            while (it != st.end()) {
                if (*it >= b) {
                    break;
                }
                it = st.erase(it);
            }
            ans.push_back(st.size());
        }
        return ans;
    }
};
