class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; ++i) {
            s.insert(i);
        }
        vector<int> result;
        for (auto& q : queries) {
            auto iter = s.lower_bound(q[0]);
            if (*iter == q[0]) ++iter;
            while (*iter < q[1]) {
                s.erase(iter++);
            }
            result.push_back(s.size()-1);
        }
        return result;
    }
};
