class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i=0;i<n;i++){
            st.insert(i);
        }
        vector<int> ans;
        for(auto q:queries){
            int u=q[0],v=q[1];
            auto lb=st.upper_bound(u);
            while(lb!=st.end() and (*lb)<v){
                int val=*lb;
                st.erase(val);
                lb=st.lower_bound(val);
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
