class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<vector<int>>store;
        vector<int>out;
        int cur=n-1;
        set<int>ss;
        for(int i=0;i<n;i++){ss.insert(i);}
        for(auto it:queries){
            int k=it[0]+1;
            vector<int>del;
            auto ptr=ss.lower_bound(k);
            k=*ptr;
            while(k<it[1]&&k<n&&ptr!=ss.end()){
                del.push_back(k);
                ptr++;
                k=*ptr;
            }
            for(auto it:del){ss.erase(it);}
            out.push_back(ss.size()-1);
        }
        return out;
    }
};
