class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {

        TreeMap<Integer, Integer> map = new TreeMap<>();
        for (int i = 1; i < n; i++) {
            map.put(i - 1, i);
        }



        int m = queries.length;
        int[] ans = new int[m];
        for (int i = 0; i < m; i++) {
            int l = queries[i][0], r =queries[i][1];
            boolean tag = false;
            while (!map.isEmpty() && map.floorKey(r - 1) != null) {
                int k = map.floorKey(r - 1);
                if (k >= l) {
                    tag = true;
                    map.remove(k);
                } else {
                    break;
                }
            }
            if (tag) {
                map.put(l, r);
            }
            ans[i] = map.size();
        }
        return ans;
    }

    class UnionFind {
        int[] parent;
        int size;
        int[] counts;

        UnionFind(int n) {
            size = n;
            parent = new int[n];
            Arrays.setAll(parent, i -> i);
            counts = new int[n];
            Arrays.fill(counts, 1);
        }

        int find(int x) {
            while (parent[x] != x) {
                x = parent[x] = parent[parent[x]];
            }
            return x;
        }

        boolean union(int x, int y) {
            x = find(parent[x]);
            y = find(parent[y]);
            if (x == y) {
                return false;
            }
            parent[x] = y;
            counts[y] += counts[x];
            size--;
            return true;
        }

        int getSize() {
            return size;
        }

        int getCount(int x) {
            return counts[find(x)];
        }

        boolean connect(int x, int y) {
            return find(parent[x]) == find(parent[y]);
        }

        void set(int x, int fa) {
            parent[x] = fa;
        }
    }


}
