class Solution {
    public class ListNode {
        public int val;
        public ListNode next;

        public ListNode() {
            next = null;
        }

        public ListNode(int val) {
            this.val = val;
        }

        public ListNode(int val, ListNode next) {
            this.val = val;
            this.next = next;
        }
    }

    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        ListNode head = new ListNode();
        ListNode p = head;
        Map<Integer, ListNode> number2ListNode = new HashMap<>();
        for (int i = 0; i < n; i++) {
            ListNode now = new ListNode(i);
            p.next = now;
            p = now;
            number2ListNode.put(i, now);
        }
        int queryNum = queries.length;
        int[] ans = new int[queryNum];
        for (int i = 0; i < queryNum; i++) {
            int[] query = queries[i];
            updateListNode(head, number2ListNode, query[0], query[1]);
            ans[i] = number2ListNode.size() - 1;
        }
        return ans;
    }

    private void updateListNode(ListNode head,
                                Map<Integer, ListNode> number2ListNode,
                                Integer left,
                                Integer right) {
        if (!number2ListNode.containsKey(left) ||
                !number2ListNode.containsKey(right)) {
            return;
        }
        ListNode leftNode = number2ListNode.get(left);
        ListNode rightNode = number2ListNode.get(right);
        if (leftNode.val == rightNode.val) {
            return;
        }
        ListNode p = leftNode;
        while (p.next != rightNode) {
            ListNode next = p.next;
            p.next = next.next;
            number2ListNode.remove(next.val);
        }
    }
}
