class Solution {
public:
    struct LinkedList{
        set <int> v;
        LinkedList(int n){
            for (int i = 0; i < n; ++i){
                v.insert(i);
            }
        }
        int add(int a, int b){
            auto vptr = v.upper_bound(a);
            vector <int> delq;
            while (*vptr < b){
                delq.push_back(*vptr);
                ++vptr;
            }
            for (int el : delq){
                v.erase(el);
            }
            return v.size() - 1;
        }

    };
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        LinkedList mylist = LinkedList(n);
        vector <int> ans;
        for (auto q : queries){
            ans.push_back(mylist.add(q[0], q[1]));
        } 
        return ans;
    }
};
