using ll = long long ;
using ii = pair<ll,ll>;

const ll mod = 1e9+7;

#define endl "\n"
#define pb push_back
#define F first
#define S second
#define MP make_pair
#define len(x) ((ll)((x).size()))
#define rep(i,x) for(ll i=0;i<(x);i++)
#define all(x) (x).begin(),(x).end()


class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        rep(i,n)st.insert(i);
        vector<int> ans;
        for(auto x:queries){
            auto it=st.lower_bound(x[0]+1);
            vector<int> tmp;
            while(it!=st.end() && (*it)<x[1]){
                tmp.pb(*it);
                it++;
            }
            for(auto x:tmp)st.erase(x);
            ans.pb(len(st)-1);
        }
        return ans;
    }
};
