class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int q = queries.length;
        int[] ans = new int[q];
        TreeSet<Integer> set = new TreeSet<>();
        for(int i=0;i<n;i++)set.add(i);
        for(int i=0;i<q;i++){
            int end = queries[i][1];
            Integer next = set.ceiling(queries[i][0]+1);
            while(next != null && next < end){
                set.remove(next);
                next = set.ceiling(next);
            }
            ans[i] = set.size()-1;
        }

        return ans;
    }
}
