class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>ans;
        set<int>st;
        for(int i = 0; i<n; i++){
            st.insert(i);
        }

        for(auto it : queries){
            auto it2 = st.lower_bound(it[0]);
            it2++;
            while(it2 != st.end() && *it2 < it[1]){
                int e = *it2;
                it2++;
                st.erase(e);
            }

            ans.push_back(st.size()-1);
        }


        return ans;
    }
};
