class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> stt;
        for (int i = 0; i < n; ++i)
            stt.insert(i);

        vector<int> res;
        for (auto &q: queries) {
            int u = q[0], v = q[1];
            if (stt.count(u)) {
                vector<int> tmp;
                int st = u;
                while (true) {
                    auto nxt = stt.upper_bound(st);
                    if (nxt == stt.end() || *nxt >= v)
                        break;
                    st = *nxt;
                    stt.erase(nxt);
                }
            }
            res.push_back((int)stt.size() - 1);
        }
        return res;
    }
};
