class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> s;
        for (int i = 0; i < n; i ++)    s.insert(i);

        int m = q.size();
        vector<int> res;
        for (int i = 0; i < m; i ++) {
            int l = q[i][0], r = q[i][1];
            while (1) {
                auto it = s.upper_bound(l);
                if (it == s.end() || *it >= r)  break;
                // if (*it > r)  break;

                // cout << *it << ' ';
                s.erase(*it);
            }
            res.push_back((int)s.size() - 1);
        }
        return res;
    }
};
