class Solution {
public:



    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m = q.size();
        vector<int> p(n);

        // function<int(int)> find = [&](int x){
        //     if(p[x] == x) return p[x];
        //     return p[x] = find(p[x]);
        // };

        for(int i = 0; i < n - 1; i ++) p[i] = i + 1;
        vector<int> ans(m);
        int cur = n - 1;
        for(int i = 0; i < m; i ++ ){
            int l = q[i][0], r = q[i][1];
            vector<int> tp;
            for(int j = l; p[j] < r; j = p[j]){
                cur --;
                tp.push_back(j);
            }
            for(auto x : tp) p[x] = r;
            ans[i] = cur;
        }
        return ans;
    }
};
