class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> collect;
        for (int i = 0; i < n; ++i) {
            collect.insert(i);
        }
        int m = queries.size();
        vector<int> res(m);
        for (int i = 0; i < m; ++i) {
            int u = queries[i][0], v = queries[i][1];

            auto it = collect.find(u);
            while (it != collect.end()) {
                if (*it >= v) {
                    break;
                }
                if (*it == u) {
                    it++;
                    continue;
                }
                collect.erase(it++);
            }
            res[i] = collect.size() - 1;
        }
        return res;
    }
};
