class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> nodes;
        for (int i = 0; i < n; i++) {
            nodes.insert(i);
        }
        vector<int> ans; ans.reserve(queries.size());
        for (const auto &q : queries) {
            int x = q[0];
            int y = q[1];
            auto iterX = nodes.upper_bound(x);
            while (iterX != nodes.end()) {
                if (*iterX < y) {
                    iterX = nodes.erase(iterX);
                } else {
                    break;
                }
            }
            ans.push_back(nodes.size() - 1);
        }
        return ans;
    }
};
