#include <bits/stdc++.h>
class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> d(n,1);
        vector<int> re;
        int cur=n-1;
        set<int> skip;
        for (auto &q:queries){
            int u=q[0],v=q[1];
            if (!skip.count(u) && v-u>d[u]){
                int curi=u;
                while (curi!=v) {
                    if (curi!=u) skip.insert(curi);
                    cur-=1;
                    curi=curi+d[curi];
                }
                cur+=1;
                d[u]=v-u;
            }
            re.push_back(cur);
        }
        return re;
    }
};
