class Solution 
{
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) 
    {
        set<int> s;
        for(int i=0;i<n;i++) s.insert(i);
        vector<int> ans;
        for(auto &q : queries)
        {
            int l=q[0];
            int r=q[1];
            // delete nums between l and r
            auto it=s.upper_bound(l);
            while(it!=s.end() && *it<r)
            {
                it=s.erase(it);
            }
            // insert r+1
            // s.insert(r+1);
            // insert l-1
            // s.insert(l-1);
            // find min distance
            ans.push_back(s.size()-1);
            // for(int i:s) cout << i << " ";
            // cout <<endl; 
        }
        return ans;
    }
};
