import java.util.TreeSet;

class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeSet<Integer> st = new TreeSet<>();
        int[] ans = new int[queries.length];

        for (int i = 0; i < n; i++){
            st.add(i);
        }

        int idx = 0;

        for (int[] q : queries) {
            Integer i = st.floor(q[0]);
            Integer j = st.ceiling(q[1]);
            st.subSet(i + 1, true, j - 1, true).clear();
            ans[idx] = st.size()-1;
            idx++;
        }

        return ans;
    }
}
