int nt[100005];
class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int i,j,k;
        vector<int> res;
        int m = q.size();
        for (i=0;i<n;++i) {
            nt[i] = i + 1;
        }
        int ans = n - 1;
        for (i=0;i<m;++i) {
            int l = q[i][0];
            int r = q[i][1];
            if (nt[l] == -1) {
                res.push_back(ans);
                continue;
            }
            if (nt[l] >= r) {
                res.push_back(ans);
                continue;
            }
            ans -= r - l - 1;
            for (j = l; j < r; ) {
                int jj = nt[j];
                nt[j] = -1;
                //printf("%d %d %d\n", i, jj, j);
                ans += (jj - j - 1);
                j = jj;
            }
            nt[l] = r;
            res.push_back(ans);
        }
        return res;

    }
};
