class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for (int i=0; i<n; i++) {
            st.insert(i);
        }
        vector<int> ans;
        for (auto& i:queries) {
            int l = i[0], r = i[1];
            if (st.count(r)) {
                auto lit = st.upper_bound(l);
                auto rit = st.lower_bound(r);
                while (lit!=rit) {
                    st.erase(lit++);
                }
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
