public class Solution {
    public int[] ShortestDistanceAfterQueries(int n, int[][] qs) {
        int N = qs.Length;
        int[] ans = new int[N];
        Dictionary<int, int> path = new Dictionary<int, int>();
        for (int i = 0; i< n; i ++){
            path.Add(i, i + 1);
        }

        for (int i = 0; i < N; i ++){
            int s = qs[i][0];
            int e = qs[i][1];

            if (path.ContainsKey(s) && path.ContainsKey(e)){
                // delete all inter nodes
                int next = path[s];
                while (next != e){
                    int nn = path[next];
                    path.Remove(next);
                    next = nn;
                }
                path[s] = e;
            }
            ans[i] = path.Keys.Count - 1;
        }
        return ans;
    }
}
