class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        d = list(range(n))
        ans = []
        for i, j in queries:
            l = bisect.bisect_left(d, i)
            r = bisect.bisect_left(d, j)
            if d[l] != i or d[r] != j:
                ans.append(len(d) - 1)
                continue
            for k in range(r - 1, l , -1):
                d.pop(k)
            ans.append(len(d) - 1)
        return ans
