from sortedcontainers import SortedList, SortedSet
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        right = dict()
        for i in range(n - 1):
            right[i] = i + 1
        cur = n - 1
        ans = []
        for x, y in queries:
            if right[x] != -1 and right[x] < y:
                z = right[x]
                while z != y:
                    right[z], z = -1, right[z]
                    cur -= 1
                right[x] = y
            ans.append(cur)

        return ans
