class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i = 0; i<n; i++) s.insert(i);
        int q = queries.size();
        vector<int> ans;
        for(int i = 0; i<q; i++) {
            int start = queries[i][0], end = queries[i][1];
            auto k = s.upper_bound(start);
            auto t = s.lower_bound(end);
            vector<int> toBeRemoved;
            for(auto p = k; p != t; p++) {
                toBeRemoved.push_back(*p);
            }
            for(auto p : toBeRemoved) s.erase(s.find(p));
            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};
