class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> nodes;
        for(int i = 0; i < n; i++) {
            nodes.insert(i);
        }
        vector<int> res;
        for(auto& q: queries) {
            auto it = nodes.lower_bound(q[0] + 1);
            while(*it < q[1]) {
                it = nodes.erase(it);
            }
            res.push_back(nodes.size() - 1);
        }
        return res;
    }
};
