function shortestDistanceAfterQueries(n: number, g: number[][]): number[] {
  const r = [];
  const m = new Array(n).fill(0).map((_, i) => i + 1);
  let nr = n - 1;

  for (const [a, b] of g) {
    if (m[a] !== -1) {
      let i = m[a];
      let k = 0;
      let j = 0;

      while (true) {
        if (i === b) {
          nr -= k;
          m[a] = b;
          break;
        } else if (i < b) {
          j = i;
          i = m[i];
          m[j] = -1;
          k++;
        } else {
          break;
        }
      }
    }

    r.push(nr);
  }

  return r;
}
