class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int colapsed = 0;
        bool gone[n];
        memset(gone, 0, sizeof(gone));
        vector<int> ret;
        int next[n];
        for (int i = 0; i < n; i++) next[i] = i+1;
        for (auto &q: queries) {
            int x = q[0];
            int y = q[1];
            if (gone[x] || gone[y] || next[x] == y) {
                ret.push_back(ret.back());
                continue;
            }

            for (int i = next[x]; i != y; i = next[i]) {
                gone[i] = true;
                colapsed++;
            }
            next[x] = y;
            ret.push_back(n - 1 - colapsed);
        }
        return ret;
    }
};
