class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qr) {
        set<pair<int, int>> s;
        int save = 0;
        vector<int> ans;
        for (auto me : qr) {
            int l = me[0];
            int r = me[1];
            if (l > r) swap(l, r);
            auto it = s.upper_bound({l, n});
            if (it != s.begin()) {
                it = prev(it);
                if (it->first <= l && it->second >= r) {
                    ans.push_back(n - 1 - save);
                    continue;
                }
            }
            while (1) {
                auto it = s.lower_bound({l, -1});
                if (it == s.end() || it->second > r) {
                    break;
                }
                save -= it->second - it->first - 1;
                s.erase(it);
            }
            save += r - l - 1;
            s.insert({l, r});
            ans.push_back(n - 1 - save);
        }
        return ans;
    }
};
