class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qry) {
        set<array<int, 2>> s;
        for (int i = 0; i < n - 1; i++) {
            s.insert({i, i + 1});
        }
        int sum = 0;
        vector<int> ans;
        for (int i = 0; i < qry.size(); i++) {
            int a = qry[i][0], b = qry[i][1];
            while (1) {
                auto it = s.lower_bound({a, 0});
                if (it != s.end() && (*it)[1] <= b) {
                    sum -= (*it)[1] - (*it)[0] - 1;
                    s.erase(it);
                } else break;
            }
            auto it = s.upper_bound({a, n});
            if (it == s.begin() || (*prev(it))[1] < b) {
                s.insert({a, b});
                sum += b - a - 1;
            }
            ans.push_back(n - 1 - sum);
        }
        return ans;
    }
};
