typedef pair<ll,ll> P;

class Solution {
public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
    vector<int> ans;
    ll sum=n-1;
    set<P> s;
    rep(i,n) s.in(P(i,i+1));
    rep(i,q.size()) {
      ll x=q[i][0],y=q[i][1];
      iter(s) it=s.lower_bound(P(x+1,-1));
      it--;
      P p=*it;
      if(p.F<=x&&y<=p.S) {
        ans.pb(sum);
        continue;
      }
      sum++;
      vector<P> v;
      it=s.lower_bound(P(x,-1));
      while(x<=(*it).F&&(*it).S<=y) {
        sum--;
        v.pb(*it);
        it++;
      }
      rep(j,v.size()) s.erase(v[j]);
      s.in(P(x,y));
      ans.pb(sum);
    }
    return ans;
  }
};
