from sortedcontainers import SortedDict, SortedSet


class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ret = []

        s = SortedSet()
        k = n - 1

        for q in queries:
            i = s.bisect_left((q[0], 9999999))
            if i > 0 and s[i - 1][1] >= q[1]:
                pass
            else:
                i = s.bisect_left((q[0], -1))
                while i < len(s) and s[i][1] <= q[1]:
                    k += s[i][1] - s[i][0] - 1
                    s.pop(i)

                s.add((q[0], q[1]))
                k -= q[1] - q[0] - 1

            ret.append(k)

        return ret
