class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        // Nothing crosses
        set<int> st;
        for(int i=0;i<n;++i)st.insert(i);
        vector<int> res;
        for(auto &q:queries){
            if(!st.count(q[0])||!st.count(q[1])){
                res.push_back((int)st.size()-1);
                continue;
            }else{
                auto it=st.lower_bound(q[0]);
                vector<int> toErase;    ++it;
                for(auto it2=it;it2!=st.end();++it2){
                    if(*it2==q[1])break;
                    else{
                        toErase.push_back(*it2);
                    }
                }
                for(auto &e:toErase)st.erase(e);
                res.push_back((int)st.size()-1);
            }
        }
        return res;
    }
};
