class Solution:
    def shortestDistanceAfterQueries(self, n, queries):
        def f(x):
            if fh[x] == x: return x
            fh[x] = f(fh[x])
            return fh[x]
        fh = [0] * (n+100005)
        a, count = [], n-1
        for i in range(n): fh[i] = i

        for l, r in queries:
            if l < r-1:
                while f(l) != f(r-1):
                    pl = f(l)
                    pl1 = f(pl+1)
                    fh[pl] = pl1
                    l = pl1
                    count -= 1
            a.append(count)
        return a
