class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& Q) {
        int N = Q.size();
        vector<int> ans;
        set<int> s;
        for(int i = 0; i < n - 1; i++){
            s.insert(i);
        }
        for(int i = 0; i < N; i++){
            int u = Q[i][0], v = Q[i][1];
            if(s.count(u)) {
                auto start = s.lower_bound(u);
                start = next(start, 1);
                auto end = s.lower_bound(v);
                end = prev(end, 1);

                if(start != s.end() && *start <= *end){
                    while(1){
                        if(start == end){
                            auto c = start;
                            start = next(start, 1);
                            s.erase(c);
                            break;
                        }
                        else{
                            auto c = start;
                            start = next(start, 1);
                            s.erase(c);
                        }
                    }
                }
            }
            int size = s.size();
            ans.push_back(size);
        }
        return ans;
    }
};
