class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int,int> m;
        for(int i=0;i<n;i++){
            m[i]=i+1;
        }
        int len=queries.size();
        vector<int> arr=vector<int>(len,0);
        for(int i=0;i<len;i++){
            if(m.count(queries[i][0])&&queries[i][1]>m[queries[i][0]]){
                m[queries[i][0]]=queries[i][1];
                auto iter=m.find(queries[i][0]);
                iter++;
                while(iter!=m.end()&&iter->first<queries[i][1]){
                    int tem=iter->first;
                    iter++;
                    m.erase(tem);
                }
            }
            arr[i]=m.size()-1;
        }
        return arr;
    }
};
