function shortestDistanceAfterQueries(n: number, queries: number[][]): number[] {
  const next: number[] = new Array(n).fill(0).map((_, i) => i + 1);
  const used: boolean[] = new Array(n).fill(false);
  next.pop();
  var res: number[] = [];
  var remain = n - 1;

  for (var [u, v] of queries) {
    if (used[u]) {
      res.push(remain);
      continue;
    }
    var p = u;
    while (p !== v && p !== n - 1) {
      if (p !== u) {
        remain--;
      }
      var p = next[p];
      if (p !== v && p !== n - 1) {
        used[p] = true;
      }
    }
    next[u] = p;
    res.push(remain);
  }
  return res;
}
