from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        s = SortedList()
        ans = []
        for i in range(n):
            s.add(i)
        for x,y in queries:
            if s.count(x) and s.count(y):
                ix, iy = s.index(x), s.index(y)
                for i in range(iy - 1, ix, -1):
                    s.pop(i)
            ans.append(len(s)-1)
        return ans
