class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        vector<int> ans;
        for(int i=1;i<n;++i) st.insert(i);
        for(auto &i: queries) {
            auto it1 = st.upper_bound(i[0]);
            auto it2 = st.lower_bound(i[1]);
            st.erase(it1, it2);
            ans.push_back(st.size());
        } 
        return ans;
    }
};
