class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> an;
        set<int> s;
        for(int i=1;i<n;i++) {
            s.insert(i);
        }
        for(auto i:q) {
            auto t = s.upper_bound(i[0]);
            vector<int> v;
            while(t!=s.end()&& (*t)<i[1]) {
                v.push_back(*t);
                t++;
            }
            for(auto i:v) s.erase(i);
            an.push_back(s.size());
        }
        return an;
    }
};
