class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>ans;
        int res=n-1;
        vector<int>d1(n);
        vector<int>d2(n);
        for(int i=0;i<n;i++){
            d1[i]=i;
            d2[i]=n-i-1;
        }
        set<pair<int,int>>s;
        for(int i=0;i<n-1;i++)s.insert({i,i+1});
        for(auto x:queries){
            int u=x[0],v=x[1];
            auto it=s.lower_bound({u,0});
            if(it!=s.begin()){
                it--;
            }
            bool flag=false;
            while(it!=s.end()){
                int l=it->first;
                int r=it->second;
                if(r<=u)it++;
                else if(l>=v)break;
                else if(l>=u&&r<=v){
                    it=s.erase(it);
                }
                else if(l<=u&&r>=v){
                    flag=true;
                    break;
                }
            }
            if(!flag)s.insert({u,v});
            ans.push_back(s.size());
        }
        return ans;
    }
};
