class Solution {
public:

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        std::vector<int> ne(n+2),st(n+2,0);
        for(int i=0;i<n;i++) ne[i]=i+1;
        ne[n-1]=-1;
        int res=n-1;
        std::vector<int> ans;
        for(auto x:queries){
            int u=x[0],v=x[1];
            if(!st[u] and ne[u]<=v){
                while(ne[u]!=v){
                    int t=ne[u];
                    st[t]=1;
                    ne[u]=ne[t];
                    res--;
                    if(ne[u]==-1) break;
                }
            }
            // for(int i=0;i<n;i++) std::cout<<st[i]<<" ";
            // std::cout<<"\n";
            ans.push_back(res);
        }
        return ans;
    }
};
