class Solution {
public:
    set<pair<int, int>> s;
    int insert(int x, int y) {
        int maxi=y-x;
        auto itr=s.lower_bound({x, -1});
        while(itr!=s.begin()) {
            itr--;
            auto nn=*itr;
            if(nn.second>x)
                maxi=max(maxi, nn.second-nn.first);
            else {
                break;
            }
        }
        itr=s.lower_bound({x, -1});
        while(itr!=s.end()) {
            auto nn=*itr;
            if(nn.first<y) {
                maxi=max(maxi, nn.second-nn.first);
            } else {
                break;
            }
            itr++;
        }
        int changeinsol=0;
        if(maxi==y-x) {
            auto itr=s.lower_bound({x, -1});
            while(itr!=s.begin()) {
                itr--;
                auto nn=*itr;
                if(nn.second>x) {
                    changeinsol+=(nn.second-nn.first-1);
                    s.erase(itr);
                    itr=s.lower_bound({x, -1});
                }else{
                    break;
                }
            }
            itr=s.lower_bound({x, -1});
            while(itr!=s.end()) {
                auto nn=*itr;
                if(nn.first<y) {
                  changeinsol+=(nn.second-nn.first-1);
                  s.erase(itr);
                  itr=s.lower_bound({x, -1});  
                }else{
                    break;
                }
            }
            changeinsol-=(y-x-1);
            s.insert({x, y});
        }
        // cout<<changeinsol<<endl;
        return changeinsol;
    }
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int ans=n-1;
        vector<int> res;
        for(int i=0;i<queries.size();i++) {
            int val=insert(queries[i][0], queries[i][1]);
            ans+=val;
            res.push_back(ans);
        }
        return res;
    }
};
