class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> intv;
        for(int i = 0 ; i <n;++i) intv.insert(i);
        vector<int> ans;
        int cans = n- 1;
        for(auto c: queries) {
            int l = c[0], r = c[1];
            if(intv.find(l) == intv.end() || intv.find(r) == intv.end()) {
                ans.push_back(cans);
                continue;
            }

            auto itL = intv.find(l);
            ++itL;
            auto itR = intv.find(r);
            intv.erase(itL,itR);
            cans = intv.size() - 1;
            ans.push_back(cans);    
        }
        return ans;
    }
};
