class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_set<int> st;
        vector<int> nxt(n);
        for (int i=0; i<n; i++){
            st.insert(i);
            nxt[i] = i+1;
        }

        vector<int> ans;
        for (auto &query: queries){
            int from = query[0], to = query[1];
            int idx = nxt[from];
            while (idx<=to-1){
                if (!st.count(idx)) break;
                st.erase(idx);
                idx = nxt[idx];
            }
            nxt[from] = max(nxt[from], to);
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
