class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        m = len(queries)
        ans = []
        intervals = dict()
        for i in range(n - 1):
            intervals[i] = i + 1
        for i in range(m):
            u,v = queries[i]
            now = u
            if now in intervals:
                while now in intervals and v >= intervals[now]:
                    nxt = intervals[now]
                    intervals.pop(now)
                    now = nxt
                intervals[u] = v
            ans.append(len(intervals))
        return ans
