const int N = 1e5 + 5;
int v[4 * N], lazy[4 * N];
int x, y, z;

void build(int id, int cx, int cy) {
	if (cx == cy) {
		v[id] = 1;
		lazy[id] = 0;
		return;
	}
	int mid = (cx + cy) / 2;
	build(2 * id, cx, mid);
	build(2 * id + 1, mid + 1, cy);

	lazy[id] = 0;
	v[id] = v[2 * id] + v[2 * id + 1];
}

void upd(int id, int cx, int cy) {
	if (y<cx or x>cy) return;

	if (x <= cx and cy <= y) {
		lazy[id] = 1;
		v[id] = 0;
		return;
	}
	int mid = (cx + cy) / 2;
	upd(2 * id, cx, mid);
	upd(2 * id + 1, mid + 1, cy);
	v[id] = v[2 * id] + v[2 * id + 1];
	if (lazy[id]) v[id] = 0;
}

int qry(int id, int cx, int cy) {
	if (y<cx or x>cy) return 0;
	if (x <= cx and cy <= y) return v[id];
	int mid = (cx + cy) / 2;
	if (lazy[id]) return 0;
	return qry(2 * id, cx, mid) + qry(2 * id + 1, mid + 1, cy);
}

class Solution {
public:

	vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
		build(1, 0, n - 1);
		vii ans;
		for (auto &i : queries) {
			x = i[0] + 1, y = i[1] - 1;
			if (x <= y)	upd(1, 0, n - 1);
			ans.pb(v[1] - 1);
		}
		return ans;
	}
};
